  render() {
    const { fill } = this.state;
    const { id, d } = this.props;

    return (
      <svg>
        <g key={`g-${id}`}>
          <path
            key={`path-${id}`}
            d={d}
            className={`province province__${id}`}
            fill={fill}
            stroke="#838383"
            strokeWidth={1.5}
            onClick={() => this.handleClick(id)}
