import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import DonutJSON from "./PieChartData.json";
import * as d3 from "d3";
import WordCloud from "./WordCloud";
import TimeSeriesChart from "./TimeSeriesChart";
import TopicJSON from "./topics.json";
const colors = [ 'red' , 'blue', 'green']

const topics = TopicJSON;
class PieChart extends Component {

  constructor(props) {
    super(props);
    this.chRef = React.createRef();

    console.log("DEEPUUUU " +this.props.location);
  }

componentDidMount(){
    this.drawChart()

}

drawChart(){

    const data = DonutJSON;
    let usableData = []
    for(let i=0; i<=15; i++){
        if(data[i]){
            if(data[i].location === this.props.location.state){

            usableData.push(data[i]);
            }
        }
    }
     console.log(usableData);
    // set the dimensions and margins of the graph
    var width = 450, height = 450, margin = 40

// The radius of the pieplot is half the width or half the height (smallest one). I subtract a bit of margin.
const radius = Math.min(width, height) / 2 - margin;

// append the svg object to the div called 'my_dataviz'
const svg = d3.select(this.chRef.current)
  .append("svg")
    .attr("width", width)
    .attr("height", height)
  .append("g")
    .attr("transform", `translate(${width/2}, ${height/2})`);

// Create dummy data
// const data = {a: 9, b: 20, c:30, d:8, e:12}

// set the color scale
const color = ["#98abc5", "#8a89a6", "#7b6888"]
// Compute the position of each group on the pie:
const pie = d3.pie()
  .value(d => d.Percentage )  
const data_ready = pie(usableData)

var tooltip = d3.select("body")
    .data(data_ready)
    .append("div")
    .style("position", "absolute")
    .style("z-index", "10")
    .style("visibility", "hidden")
    .style("background-color", "silver")
    .text((d) => d.data.Percentage*100 + "% "+ d.data.Type);

svg
  .selectAll()
  .data(data_ready)
  .join('path')
  .attr('d', d3.arc()
    .innerRadius(0)
    .outerRadius(radius)
  )
  .attr('fill', (d) =>  colors[d.index])
  .attr("stroke", "black")
  .style("stroke-width", "2px")
  .style("opacity", 0.7)
  .on("mouseover", function(d){
           d3.select(this).attr('d',function(d){ return d3.arc().innerRadius(0)
          .outerRadius(180)(d)});
          tooltip.style("visibility", "visible")
          console.log(this.chRef);
          tooltip.text((d) => d.data.Percentage*100 + "% ")
          tooltip.style("visibility", "visible")})
  .on("mousemove", function(event){return tooltip.style("top", (event.pageY-10)+"px").style("left",(event.pageX+10)+"px");})
  .on("mouseout", function(){
    d3.select(this).attr('d',function(d){return d3.arc().innerRadius(0).outerRadius(radius)(d)});
                    tooltip.style("visibility", "hidden");
  })
  .call(d3.zoom().on("zoom", function (event) {
    svg.attr("transform", event.transform)
 }))

  // shape helper to build arcs:
var arcGenerator = d3.arc()
.innerRadius(0)
.outerRadius(radius)

svg
  .selectAll('mySlices')
  .data(data_ready)
  .enter()
  .append('text')
  .text(function(d){ return d.data.Type})
  .attr("transform", function(d) { return "translate(" + arcGenerator.centroid(d) + ")";  })
  .style("text-anchor", "middle")
  .style("font-size", 17)
}

render() {
    return (
      <>
        {/* <h1>{this.props.location.state}</h1> */}
        <div className="row">
            <div className="col-md-4">
                <h1 style={{textAlign:"center"}}>Pie Chart</h1>
                <div ref={this.chRef}></div>

            </div>
            {/* <div className="col-md-4">
            <WordCloud
                  topics={topics}
                />
                  </div>
            <div className="col-md-4">
            <TimeSeriesChart />
            </div>     */}
        </div>
        {/* <script src="https://d3js.org/d3.v6.js"></script>
        <script src="/Users/deeppatel/Desktop/Visualization/Project/csci6406_project/node_modules/d3/dist/d3.js"></script>
        <script src="/Users/deeppatel/Desktop/Visualization/Project/csci6406_project/node_modules/d3moji/d3moji.js"></script> */}
      </>
    );
  }
}

export default withRouter(PieChart);
