import React, { Component } from "react";
import "./CanadaMap.css";
import { DropdownButton, Dropdown, ButtonGroup } from "react-bootstrap";
import Form from 'react-bootstrap/Form'
import Province from "./Province";

class CanadaMap extends Component {
  constructor(props) {
    super(props);
    this.state = {
      viewBox: this.props.viewBox,
      selected: null
    };
    this.onSelect = this.onSelect.bind(this);
  }

  onSelect(id) {
    this.setState({ selected: id });
  }

  render() {
    const { mapJson, width, height } = this.props;
    const { viewBox, selected } = this.state;

    return (
      <>
      <div className="row">
        <div className="col-md-4">
          <h1>{this.state.selected ? this.state.selected : "Canada"}</h1>
          <h2>
            Data Visualization on the Sentimental Analysis of Tweeter Data
          </h2>
          <Form.Group controlId="custom-multi-select">
               <Form.Label>Select Data</Form.Label>
               <Form.Control as="select" multiple className="rounded-0 shadow">
                    {["1", "2", "3", "4", "5"].map(option => (
                         <option key={option}>Option {option}</option>
                    ))}
               </Form.Control>
          </Form.Group>
        </div>
        <div className="col-md-8"> 
          <svg width={width} height={height} viewBox={viewBox}>
            {mapJson.map((province) => (
              <Province
                id={province.id}
                d={province.d}
                onSelect={this.onSelect}
                selected={selected}
                key={`province-${province.id}`}
              />
            ))}
          </svg>
        </div>
      </div>
      </>
    );
  }
}

export default CanadaMap;
