import React, { Component, useState, useEffect } from "react";
import "./CanadaMap.css";
import Province from "./Province";
import { withRouter } from "react-router-dom";
import DonutJSON from "./PieChartData.json";
import PieClass from "./PieClass";
import * as d3 from "d3";

class PieChart extends Component {

  constructor(props) {
    super(props);
    this.chRef = React.createRef();
  }

componentDidMount(){
    this.drawChart()
}
// A function that create / update the plot for a given variable:
update(data) {

    // Compute the position of each group on the pie:
    var pie = d3.pie()
      .value(function(d) {return d.value; })
      .sort(function(a, b) { console.log(a) ; return d3.ascending(a.key, b.key);} ) // This make sure that group order remains the same in the pie chart
    var data_ready = pie(data)

    // map to data
    var u = svg.selectAll("path")
      .data(data_ready)

    // Build the pie chart: Basically, each part of the pie is a path that we build using the arc function.
    u
      .enter()
      .append('path')
      .merge(u)
      .transition()
      .duration(1000)
      .attr('d', d3.arc()
        .innerRadius(0)
        .outerRadius(radius)
      )
      .attr('fill', function(d){ return(color(d.data.key)) })
      .attr("stroke", "white")
      .style("stroke-width", "2px")
      .style("opacity", 1)

    // remove the group that is not present anymore
    u
      .exit()
      .remove()

  }
drawChart(){
    // Create dummy data
    const data = DonutJSON;
    let usableData = {}
    for(let i=0; i<=15; i++){
        if(data[i]){
            if(data[i].location === this.props.location.state){
            usableData = data[i];
            }
        }
    }

    var width = 450, height = 450, margin = 40

// The radius of the pieplot is half the width or half the height (smallest one). I subtract a bit of margin.
var radius = Math.min(width, height) / 2 - margin

// append the svg object to the div called 'my_dataviz'
var svg = d3.select(this.chRef.current)
  .append("svg")
    .attr("width", width)
    .attr("height", height)
  .append("g")
    .attr("transform", "translate(" + width / 2 + "," + height / 2 + ")");

// create 2 data_set
var data1 = {a: 9, b: 20, c:30, d:8, e:12}
// var data2 = {a: 6, b: 16, c:20, d:14, e:19, f:12}

// set the color scale
var color = d3.scaleOrdinal()
  .domain(["a", "b", "c", "d", "e", "f"])
  .range(d3.schemeDark2);

// Initialize the plot with the first dataset
update(data1)
}

render() {
    return (
      <>
        <h1>{this.props.location.state}</h1>
        <div ref={this.chRef}></div> 
        <button onClick={update(data1)}>button1</button>
        <script src="/Users/deeppatel/Desktop/Visualization/Project/csci6406_project/node_modules/d3/dist/d3.js"></script>
        <script src="/Users/deeppatel/Desktop/Visualization/Project/csci6406_project/node_modules/d3moji/d3moji.js"></script>
      </>
    );
  }
}

export default withRouter(PieChart);
