import React from "react";
import axios from 'axios';
import CanadaMap from "./components/CanadaMap";
import "./styles.css";
import DonutChart from './components/donutChart';
import CanadaJSON from "./components/CanadaMap.json";
import DonutJSON from "./components/donutData.json";

export default function App() {
  const [visible, setVisible] = React.useState(false)
  const [show, setShow] = React.useState("Show")

  axios.get('https://api.opencovid.ca/timeseries')
  .then(res => {
    const JSONData = res.data;
  })

  function handleClick(e) {
    if(show == "Show"){
      setVisible(true)
      setShow("Hide")
    }else{
      setVisible(false)
      setShow("Show")
    }
    console.log(visible)
  }

  const Chart = () =>{
    return(
      <div>
        <p>Chart</p>
        <DonutChart data={DonutJSON} />
      </div>
    )
  }
  return (
    <div className="App">
      <button onClick={handleClick}>{show} Donut</button>
      { visible ? <Chart /> : null }

      <CanadaMap
        mapJson={CanadaJSON}
        width="760"
        height="620"
        viewBox="0 0 760 620"
      />
    </div>
  );
}
