import React, { Component } from "react";
import "./Province.css";
import * as d3 from 'd3';
const colors = [ '#8ce8ad', '#57e188', '#34c768', '#2db757', '#27acaa', '#42c9c2', '#60e6e1', '#93f0e6', '#87d3f2', '#4ebeeb', '#35a4e8', '#188ce5', '#542ea5', '#724bc3', '#9c82d4', '#c981b2', '#b14891', '#ff6d00', '#ff810a', '#ff9831', '#ffb46a', '#ff9a91', '#ff736a', '#f95d54', '#ff4136', '#c4c4cd' ];

class Province extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fill: "#FFCCCC"
    };
    this.handleClick = this.handleClick.bind(this);
    this.chRef = React.createRef();

  }

  componentDidUpdate() {
    if (this.props.selected !== this.props.id) {
      if (this.state.fill === "navy") this.setState({ fill: "red" });
    }
  }

  handleClick(id) {
    this.setState({ fill: "green" });
    this.props.onSelect(id);  
    // this.drawChart()
  }




  render() {
    const { fill } = this.state;
    const { id, d } = this.props;

    return (
      <g key={`g-${id}`}>
        <path
          key={`path-${id}`}
          d={d}
          className={`province province__${id}`}
          fill={fill}
          stroke="#838383"
          strokeWidth={1.5}
          onClick={() => this.handleClick(id)}
        />
        <div ref={this.chRef}></div> 
      </g>

    );
  }
}

export default Province;
