import React, { Component, useState, useEffect } from "react";
import "./CanadaMap.css";
import Province from "./Province";
import { withRouter } from "react-router-dom";
import DonutJSON from "./PieChartData.json";
import PieClass from "./PieClass";
import * as d3 from "d3";
import {scaleLinear} from "d3-scale";
import WordCloud from "./WordCloud";
import PieChart from "./PieChart";
import TimeSeriesChart from "./TimeSeriesChart";
import TopicJSON from "./topics.json";
const colors = [ 'red' , 'blue', 'green']
const topics = TopicJSON;
class VisualizationChart extends Component {

    constructor(props){
        super(props);
        this.state = {
            PieChart: null,
            WordCloud: null,
            TimeSeriesChart: null
        };
        this.PieChartDataFunction();
        this.WorldCloudDataFunction();
        this.TimeSeriesChartDataFunction();
    }

    PieChartDataFunction(){
        const data = DonutJSON;

        console.log("STATE:----->", this.props.location)
        let usableData = []
        for(let i=0; i<=data.length; i++){
            if(data[i]){
                if(data[i].location === this.props.location.state){

                usableData.push(data[i]);
                }
            }
        }
        console.log("Data", usableData)

        this.state.PieChart = usableData;
    }

    WorldCloudDataFunction(){
        const data = TopicJSON;

        this.state.WordCloud = data;

    }

    TimeSeriesChartDataFunction(){

        const data = d3.csv("https://raw.githubusercontent.com/holtzy/data_to_viz/master/Example_dataset/3_TwoNumOrdered_comma.csv");

        this.state.TimeSeriesChart = data;
    }

    render() {


    return (
      <>
        <h1>{this.props.location.state}</h1>
        <div className="row">
            <div className="col-md-4">
                <PieChart 
                    data = {this.state.PieChart}
                />
            </div>
            <div className="col-md-4">
                <WordCloud
                    topics={this.state.WordCloud}
                />
            </div>
            <div className="col-md-4">
                <TimeSeriesChart 
                    data = {this.state.TimeSeriesChart}
                />
            </div>    
        </div>
        {/* <script src="https://d3js.org/d3.v6.js"></script>
        <script src="/Users/deeppatel/Desktop/Visualization/Project/csci6406_project/node_modules/d3/dist/d3.js"></script>
        <script src="/Users/deeppatel/Desktop/Visualization/Project/csci6406_project/node_modules/d3moji/d3moji.js"></script> */}
      </>
    );
  }
}

export default withRouter(VisualizationChart);
