import React, { Component } from "react";
import "./CanadaMap.css";
import { DropdownButton, Dropdown, ButtonGroup } from "react-bootstrap";
import Form from 'react-bootstrap/Form'
import Province from "./Province";
import "../../src/styles.css"
import * as d3 from "d3";
import { color } from "d3";

class CanadaMap extends Component {
  constructor(props) {
    super(props);
    this.state = {
      viewBox: this.props.viewBox,
      selected: null
    };
    this.onSelect = this.onSelect.bind(this);
  }

  onSelect(id) {
    this.setState({ selected: id });
  }

  render() {
    const { mapJson, width, height } = this.props;
    const { viewBox, selected } = this.state;

    return (
      <>
      <div className="row">
        <div className="col-md-4" style={{textAlign: "left"}}>
          <h2></h2>
          <h2 className="h2" 
            onMouseOver=
            { 
              d3.select('this')
              .attr("fill","")
              .classed("active", true)
            } 
            style={{fontSize:0, fontStyle:"bold"}}
          >{this.state.selected ? this.state.selected : "Canada"}</h2>
          <br /><br />
          <h2 style={{fontSize:60, paddingLeft:10}}>
            <div style={{fontFamily:"-moz-initial"}}> <b>D</b>ata <b>V</b>isualization</div> 
            <br /> 
            <div style={{fontFamily:"-moz-initial", fontSize:30}}>on the</div>  
            <br /> 

            <div style={{fontFamily:"-moz-initial"}}><b>S</b>entiment <b>A</b>nalysis</div>
            <br /> 

            <div style={{fontFamily:"-moz-initial", fontSize:30}}> of</div>
            <br /> 

            <div style={{fontFamily:"-moz-initial"}}><b>T</b>weeter <b>D</b>ata</div>
          </h2>

        </div>
        <div className="col-md-8" style={{align:"right"}}>
        <Form.Group controlId="custom-select" >
               {/* <Form.Label>Sentiment Selection</Form.Label> */}
               <Form.Control as="select" className="rounded-0 shadow">
                    <option className="d-none" value="">
                         Select Data
                    </option>
                    {["1", "2", "3", "4", "5"].map(option => (
                         <option key={option}>Option {option}</option>
                    ))}
               </Form.Control>
          </Form.Group> 
          <svg width={width} height={height} viewBox={viewBox}>
            {mapJson.map((province) => (
              <Province
                id={province.id}
                d={province.d}
                onSelect={this.onSelect}
                selected={selected}
                key={`province-${province.id}`}
              />
            ))}
          </svg>
        </div>
      </div>
      </>
    );
  }
}

export default CanadaMap;
