import React, { Component, useState, useEffect } from "react";
import "./CanadaMap.css";
import Province from "./Province";
import { withRouter } from "react-router-dom";
import DonutJSON from "./PieChartData.json";
import PieClass from "./PieClass";
import * as d3 from "d3";

class PieChart extends Component {
  constructor(props) {
    super(props);
    const data = DonutJSON;
    let usableData = {}
    for(let i=0; i<=15; i++){
        if(data[i]){
            if(data[i].location === this.props.location.state){
            // console.log(data[i].province)
            usableData = data[i];
            }
        }
    }
    console.log("PROVINCE_DATA",usableData)
  }
  render() {
    const generateData = (value, length = 5) =>
        d3.range(length).map((item, index) => ({
        date: index,
        value: value === null || value === undefined ? Math.random() * 100 : value
        }));

    const [data, setData] = useState(generateData(0));
    console.log(data);
    const changeData = () => {
        setData(generateData());
    };

    useEffect(() => {
        setData(generateData());
    }, [!data]);
    return (
      <>
        <h1>{this.props.location.state}</h1>
        <PieClass
          data={data}
          width={200}
          height={200}
          innerRadius={60}
          outerRadius={100}
        />
      </>
    );
  }
}

export default withRouter(PieChart);
